/**
 * Roll standard dice expression like "1D6+2D4-2"
 * Ported from diceRoller.js
 */
function rollDiceExpression(expression) {
  const cleanExpression = expression.toUpperCase().replace(/\s+/g, '');
  const diceRegex = /(\d+)D(\d+)/g;
  const modRegex = /([+-](?!\d*D)\d+)/g;

  let total = 0;
  let modTotal = 0;
  let output = [];

  // Roll all dice
  let diceMatch;
  while ((diceMatch = diceRegex.exec(cleanExpression)) !== null) {
    const num = parseInt(diceMatch[1]);
    const sides = parseInt(diceMatch[2]);
    let diceRolls = [];
    let diceTotal = 0;

    for (let i = 0; i < num; i++) {
      const roll = Math.floor(Math.random() * sides) + 1;
      diceRolls.push(roll);
      diceTotal += roll;
    }

    total += diceTotal;
    output.push({
      type: `${num}D${sides}`,
      rolls: diceRolls,
      subtotal: diceTotal
    });
  }

  // Apply modifiers
  let modMatch;
  while ((modMatch = modRegex.exec(cleanExpression)) !== null) {
    const mod = parseInt(modMatch[1]);
    modTotal += mod;
  }

  total += modTotal;

  return {
    total: total,
    rolls: output,
    modifier: modTotal,
    expression: expression
  };
}

const expression = $json.expression || '1D100';
const result = rollDiceExpression(expression);

// Format for display
let formattedParts = [];
result.rolls.forEach(roll => {
  formattedParts.push(`${roll.type}: [${roll.rolls.join(', ')}] = ${roll.subtotal}`);
});

if (result.modifier !== 0) {
  formattedParts.push(`Modifier: ${result.modifier > 0 ? '+' : ''}${result.modifier}`);
}

formattedParts.push(`Total: ${result.total}`);

return [
  {
    json: {
      ...result,
      formatted: formattedParts.join('\n'),
      roll_type: 'standard'
    }
  }
];

/**
 * Roll percentage dice (D100) with bonus/penalty dice
 * Ported from diceRoller.js
 */
function rollPercentDice(numBonus = 0, numPenalty = 0) {
  const tens = Math.floor(Math.random() * 10) * 10;
  const units = Math.floor(Math.random() * 10);
  const diff = numBonus - numPenalty;

  let effectiveBonus = 0;
  let effectivePenalty = 0;
  let bonusRolls = [];
  let penaltyRolls = [];

  if (diff > 0) {
    effectiveBonus = diff;
    for (let i = 0; i < effectiveBonus; i++) {
      bonusRolls.push(Math.floor(Math.random() * 10) * 10);
    }
  } else if (diff < 0) {
    effectivePenalty = -diff;
    for (let i = 0; i < effectivePenalty; i++) {
      penaltyRolls.push(Math.floor(Math.random() * 10) * 10);
    }
  }

  let finalTens = tens;
  if (effectiveBonus > 0) {
    finalTens = Math.min(tens, ...bonusRolls);
  } else if (effectivePenalty > 0) {
    finalTens = Math.max(tens, ...penaltyRolls);
  }

  const result = finalTens + units;
  const displayResult = result === 0 ? 100 : result;

  return {
    result: displayResult,
    tens: tens,
    units: units,
    bonusRolls: bonusRolls,
    penaltyRolls: penaltyRolls,
    effectiveBonus: effectiveBonus,
    effectivePenalty: effectivePenalty
  };
}

const bonusDice = parseInt($json.bonus_dice) || 0;
const penaltyDice = parseInt($json.penalty_dice) || 0;

const result = rollPercentDice(bonusDice, penaltyDice);

// Format for display
let formattedParts = [];
formattedParts.push(`Tens: ${result.tens}`);
formattedParts.push(`Units: ${result.units}`);

if (result.effectiveBonus > 0) {
  formattedParts.push(`Bonus rolls: [${result.bonusRolls.join(', ')}]`);
}

if (result.effectivePenalty > 0) {
  formattedParts.push(`Penalty rolls: [${result.penaltyRolls.join(', ')}]`);
}

formattedParts.push(`Result: ${result.result}`);

return [
  {
    json: {
      ...result,
      formatted: formattedParts.join('\n'),
      roll_type: 'percentage'
    }
  }
];

/**
 * Roll skill check with percentage dice
 * Determines success/failure based on skill value
 */
function rollPercentDice(numBonus = 0, numPenalty = 0) {
  const tens = Math.floor(Math.random() * 10) * 10;
  const units = Math.floor(Math.random() * 10);
  const diff = numBonus - numPenalty;

  let effectiveBonus = 0;
  let effectivePenalty = 0;
  let bonusRolls = [];
  let penaltyRolls = [];

  if (diff > 0) {
