const skillName = $json.skill_name || 'Unknown Skill';
const skillValue = parseInt($json.skill_value) || 0;
const bonusDice = parseInt($json.bonus_dice) || 0;
const penaltyDice = parseInt($json.penalty_dice) || 0;
const difficulty = $json.difficulty || 'Regular';

const diceResult = rollPercentDice(bonusDice, penaltyDice);

// Determine success level
const roll = diceResult.result;
let successLevel = 'Failure';
let targetValue = skillValue;

// Apply difficulty modifiers
if (difficulty === 'Hard') {
  targetValue = Math.floor(skillValue / 2);
} else if (difficulty === 'Extreme') {
  targetValue = Math.floor(skillValue / 5);
}

// Check for critical success/fumble
if (roll === 1) {
  successLevel = 'Critical Success';
} else if (roll === 100 || (roll >= 96 && skillValue < 50)) {
  successLevel = 'Fumble';
} else if (roll <= targetValue) {
  // Determine success level
  if (roll <= Math.floor(skillValue / 5)) {
    successLevel = 'Extreme Success';
  } else if (roll <= Math.floor(skillValue / 2)) {
    successLevel = 'Hard Success';
  } else {
    successLevel = 'Success';
  }
} else {
  successLevel = 'Failure';
}

// Format for display
let formattedParts = [];
formattedParts.push(`Skill: ${skillName} (${skillValue}%)`);
formattedParts.push(`Difficulty: ${difficulty} (target: ${targetValue}%)`);
formattedParts.push(`Rolled: ${roll}`);

if (diceResult.effectiveBonus > 0) {
  formattedParts.push(`Bonus dice: [${diceResult.bonusRolls.join(', ')}]`);
}

if (diceResult.effectivePenalty > 0) {
  formattedParts.push(`Penalty dice: [${diceResult.penaltyRolls.join(', ')}]`);
}

formattedParts.push(`Result: ${successLevel}`);

return [
  {
    json: {
      skill_name: skillName,
      skill_value: skillValue,
      difficulty: difficulty,
      target_value: targetValue,
      rolled: roll,
      success_level: successLevel,
      success: ['Success', 'Hard Success', 'Extreme Success', 'Critical Success'].includes(successLevel),
      formatted: formattedParts.join('\n'),
      roll_details: diceResult,
      roll_type: 'skill_check'
    }
  }
];
