parameters.jsCode:
// Build context prompt for LLM
const question = $json.question || $json.query || '';
const collectedData = $json.collected_data || {};
const agentType = $json.agent_type || 'prophet';
const references = $json.references || [];

// Build system prompt based on agent type
let systemPrompt = '';

if (agentType === 'prophet') {
  systemPrompt = `You are the Prophet, a helpful assistant for Call of Cthulhu RPG players. Your role is to answer questions about:
- Character statistics and abilities
- Game rules and mechanics
- Turn history and past events
- Dice rolling and skill checks
- Campaign lore and story elements

IMPORTANT GUIDELINES:
- Be concise and factual
- Cite sources when available
- If data is missing or unclear, acknowledge it
- Use Call of Cthulhu terminology accurately
- Maintain the dark, mysterious tone of the setting`;

} else if (agentType === 'dungeonmaster') {
  systemPrompt = `You are the DungeonMaster agent for Call of Cthulhu. Your role:
1. Process player-declared actions
2. Resolve skill checks
3. Generate narrative reactions (NPCs, environment, outcomes)

CRITICAL RULES:
- NEVER decide what player characters do
- ONLY describe outcomes of player-declared actions
- If the scene requires player decisions, STOP and return the scene
- NPCs and environment can act autonomously
- Players control their characters exclusively

Your narrative should:
- Drive toward campaign milestones
- Incorporate Call of Cthulhu mythos and tone
- Reflect skill check outcomes
- Maintain dark, atmospheric tension`;
}

// Build user prompt with collected data
let userPromptParts = [];

if (question) {
  userPromptParts.push(`Question: ${question}`);
}

// Add character data
if (collectedData.characters && collectedData.characters.length > 0) {
  userPromptParts.push('\n=== CHARACTER DATA ===');
  collectedData.characters.forEach(char => {
    userPromptParts.push(`Character: ${char.name}`);
    if (char.occupation) userPromptParts.push(`Occupation: ${char.occupation}`);
    if (char.sanity) userPromptParts.push(`Sanity: ${char.sanity.current}/${char.sanity.max}`);
    if (char.hit_points) userPromptParts.push(`HP: ${char.hit_points.current}/${char.hit_points.max}`);
    userPromptParts.push('');
  });
}

// Add campaign data
if (collectedData.campaign) {
  const campaign = collectedData.campaign;
  userPromptParts.push('\n=== CAMPAIGN CONTEXT ===');
  if (campaign.name) userPromptParts.push(`Campaign: ${campaign.name}`);
  if (campaign.setting?.tone) userPromptParts.push(`Tone: ${campaign.setting.tone}`);
  if (campaign.setting?.goal) userPromptParts.push(`Goal: ${campaign.setting.goal}`);
  if (campaign.story_arc?.tagline) userPromptParts.push(`Story: ${campaign.story_arc.tagline}`);
  if (campaign.setting?.key_elements) {
    userPromptParts.push(`Key Elements: ${campaign.setting.key_elements.join(', ')}`);
  }
  userPromptParts.push('');
}

// Add rules/lore chunks from Qdrant
if (collectedData.rules_chunks && collectedData.rules_chunks.length > 0) {
  userPromptParts.push('\n=== RELEVANT RULES ===');
  collectedData.rules_chunks.forEach((chunk, idx) => {
    userPromptParts.push(`[${idx + 1}] ${chunk.text}`);
    userPromptParts.push(`Source: ${chunk.source}`);
    userPromptParts.push('');
  });
}

// Add turn history
if (collectedData.turns && collectedData.turns.length > 0) {
  userPromptParts.push('\n=== TURN HISTORY ===');
  collectedData.turns.forEach(turn => {
    userPromptParts.push(`Turn #${turn.turn_number}:`);
    if (turn.actions) {
      turn.actions.forEach(action => {
        if (action.speak) userPromptParts.push(`  Speak: ${action.speak}`);
        if (action.act) userPromptParts.push(`  Act: ${action.act}`);
      });
    }
    if (turn.reaction?.description) {
      userPromptParts.push(`  Reaction: ${turn.reaction.description}`);
    }
    userPromptParts.push('');
  });
}

// Add player actions (for DungeonMaster)
if (collectedData.player_actions && collectedData.player_actions.length > 0) {
  userPromptParts.push('\n=== PLAYER ACTIONS ===');
  collectedData.player_actions.forEach((action, idx) => {
    userPromptParts.push(`[${idx + 1}] ${action.character_name || 'Character'}:`);
    if (action.speak) userPromptParts.push(`  Says: "${action.speak}"`);
    if (action.act) userPromptParts.push(`  Does: ${action.act}`);
    if (action.appearance) userPromptParts.push(`  Appearance: ${action.appearance}`);
    if (action.emotion) userPromptParts.push(`  Emotion: ${action.emotion}`);
    userPromptParts.push('');
  });
}

// Add skill check results (for DungeonMaster)
if (collectedData.skill_checks && collectedData.skill_checks.length > 0) {
  userPromptParts.push('\n=== SKILL CHECK RESULTS ===');
  collectedData.skill_checks.forEach(check => {
    userPromptParts.push(`${check.character_name || 'Character'}: ${check.skill_name} - ${check.success_level} (rolled ${check.rolled} vs ${check.target_value})`);
  });
  userPromptParts.push('');
}

const userPrompt = userPromptParts.join('\n');

return [
  {
    json: {
      system_prompt: systemPrompt,
      user_prompt: userPrompt,
      question,
      references,
      agent_type: agentType
    }
  }
];

parameters.jsonBody:
={{ JSON.stringify({
  model: 'gpt-oss:20b',
  messages: [
    {
      role: 'system',
      content: $json.system_prompt
    },
    {
      role: 'user',
      content: $json.user_prompt
    }
  ],
  stream: false,
  options: {
    temperature: 0.3,
    num_predict: 1000
  }
}) }}

parameters.jsCode:
// Extract answer from Ollama response
const ollamaResponse = $json.message?.content || $json.response || '';
const references = $input.first().json.references || [];
const agentType = $input.first().json.agent_type || 'prophet';

return [
  {
    json: {
      answer: ollamaResponse,
      references: references,
      agent_type: agentType
    }
  }
];
